import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import Announcement from '../components/Announcement'
import Footer from '../components/Footer'
import Navbar from '../components/Navbar'
import Newsletter from '../components/Newsletter'
import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
import { mobile } from '../responsive'
import { useLocation } from 'react-router-dom'
import { publicRequest } from '../components/requestMethod'

const Container = styled.div`
`
const Wrapper = styled.div`
padding:50px;
display:flex;
${mobile({
    padding:"10px", flexDirection:"column"
})};
`
const ImgContainer = styled.div`
flex:1;
`
const Image = styled.img`
width:100%;
height:90vh;
object-fit:cover;
${mobile({
    height:"40vh"
})};
`
const InfoContainer = styled.div`
flex:1;
padding:0px 50px;
${mobile({
    padding:"10px"
})};
`
const Title = styled.h1`
font-weight:200;
`
const Desc = styled.p`
margin: 20px 0px;
`
const Price = styled.span`
font-weight:100;
font-size:40px;
`
const FilterContainer = styled.div`
width:70%;
margin:30px 0px;
display:flex;
justify-content: space-between;
${mobile({
    width:"100%"
})};
`
const Filter = styled.div`
display:flex;
align-item:center;
`
const FilterTitle = styled.h3`
font-size:20px;
font-weight:200;
`
const FilterColor = styled.div`
width:20px;
height:20px;
border-radius:50%;
background-color: ${props=>props.color};
margin:0px 5px;
cursor:pointer;
display:flex;
`
const FilterSize = styled.select`
margin-left:10px;
padding:5px;
`
const FilterSizeOption = styled.option`
`
const AddContainer = styled.div`
width:50%;
display:flex;
align-items:center;
justify-content: space-between;
${mobile({
    width:"100%"
})};
`
const AmountContainer = styled.div`
display:flex;
align-items:center;
font-weight:700;
`
const Amount = styled.span`
width:30px;
height30px;
border-radius:10px;
border:1px solid teal;
display:flex;
align-items:center;
justify-content:center;
margin: 0px 5px;
`
const Button = styled.button`
padding:15px;
border:2px solid teal;
background-color:white;
cursor:pointer;
font-weight:500;

&:hover{
    background-color:#DFB1A7;
}
`


function SingleProduct() {
    const location = useLocation();
    
    const id = location.pathname.split("/")[2];
    const [product, setProduct] = useState({})
       

    useEffect(()=>{
        const getProduct = async ()=>{
            try{
                const res = await publicRequest.get("/products/find/" + id)
                console.log(res.data);
                setProduct("data is "+res.data);
                //console.log(res.data);
            }catch{}
        };
        getProduct();
    },[id])
            
    return (
        <Container>
            <Navbar />
            <Announcement />
            <Wrapper>
    <ImgContainer>
    {/* <Image src='https://images.pexels.com/photos/870808/pexels-photo-870808.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940'/> */}
    <Image src={product.img} />
    </ImgContainer>
    <InfoContainer>
        <Title>Fresh Pepper</Title>
        <Desc>This is organic fresh pepper from Buea and it's well preserved and can be shipped out of Cameroon fresh or dried</Desc>
        <Price>50000 Fcfa</Price>
        <FilterContainer>
            <Filter>
                <FilterTitle>Color</FilterTitle>
                <FilterColor color="green" />
                <FilterColor color="red" />
                <FilterColor color="yellow" />
            </Filter>
            <Filter>
                <FilterTitle>Size</FilterTitle>
                <FilterSize>
                    <FilterSizeOption>XS</FilterSizeOption>
                    <FilterSizeOption>S</FilterSizeOption>
                    <FilterSizeOption>M</FilterSizeOption>
                    <FilterSizeOption>L</FilterSizeOption>
                    <FilterSizeOption>XL</FilterSizeOption>
                </FilterSize>
            </Filter>
        </FilterContainer>
        <AddContainer>
            <AmountContainer>
                <RemoveIcon />
                <Amount>1</Amount>
                <AddIcon />
                
            </AmountContainer>
            <Button>ADD TO CART</Button>
        </AddContainer>

    </InfoContainer>

</Wrapper>

            <Newsletter />
            <Footer />
        </Container>
    )
}

export default SingleProduct
