import React from 'react'
import styled from 'styled-components'
import Announcement from '../components/Announcement'
import Footer from '../components/Footer'
import Navbar from '../components/Navbar'
import Newsletter from '../components/Newsletter'

const Container = styled.div`
`
const Wrapper = styled.div`
padding:50px;
display:flex;
`
const ImgContainer = styled.div`
flex:1;
`
const Image = styled.img`
width:100%;
height:90vh;
object-fit:cover;
`
const InfoContainer = styled.div`
flex:1;
padding:0px 50px;
`
const Title = styled.h1`
font-weight:200;
`
const Desc = styled.p`
margin: 20px 0px;
`
const Price = styled.span`
font-weight:100;
font-size:40px;
`
const FilterContainer = styled.div`
width:50%;
margin:30px 0px;
display:flex;
justify-content: space-between;
`
const Filter = styled.div`
display:flex;
align-item:center;
`
const FilterTitle = styled.span`
font-size:20px;
font-weight:200;
`
const FilterColor = styled.div`
width:20px;
height:20px;
border-radius:50%;
background-color: ${props=>props.color};
margin:0px 5px;
cursor:pointer;
display:flex;
`
const FilterSize = styled.select`

`
const FilterSizeOption = styled.option`
`


function SingleProduct() {
    return (
        <Container>
            <Navbar />
            <Announcement />
            <Wrapper>
    <ImgContainer>
    <Image src='https://images.pexels.com/photos/870808/pexels-photo-870808.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940'/>
    </ImgContainer>
    <InfoContainer>
        <Title>Fresh Pepper</Title>
        <Desc>This is organic fresh pepper from Buea and it's well preserved and can be shipped out of Cameroon fresh or dried</Desc>
        <Price>50000 Fcfa</Price>
        <FilterContainer>
            <Filter>
                <FilterTitle>Color</FilterTitle>
                <FilterColor color="green" />
                <FilterColor color="red" />
                <FilterColor color="yellow" />
            </Filter>
            <Filter>
                <FilterTitle>Size</FilterTitle>
                <FilterSize>
                    <FilterSizeOption>XS</FilterSizeOption>
                    <FilterSizeOption>S</FilterSizeOption>
                    <FilterSizeOption>M</FilterSizeOption>
                    <FilterSizeOption>L</FilterSizeOption>
                    <FilterSizeOption>XL</FilterSizeOption>
                </FilterSize>
            </Filter>
        </FilterContainer>


    </InfoContainer>

</Wrapper>

            <Newsletter />
            <Footer />
        </Container>
    )
}

export default SingleProduct
